package reflex.app.handler;

import rapture.common.RaptureScript;
import rapture.common.client.ScriptClient;
import rapture.common.uri.URIParser;
import reflex.IReflexScriptHandler;

public class ReflexScriptHandler implements IReflexScriptHandler {
    private ScriptClient client;

    public ReflexScriptHandler(ScriptClient client) {
        this.client = client;
    }

    @Override
    public String getScript(String scriptName) {
        scriptName = URIParser.convertDocURI(scriptName);
        RaptureScript script = client.getScript().getScript(scriptName);
        if (script != null) {
            return script.getScript();
        }
        return "";
    }

    @Override
    public boolean hasCapability() {
        return true;
    }
    
}
