package rapture.repo.integration;

import rapture.common.MongoDbTests;
import rapture.repo.VersionedRepoTest;

import org.apache.commons.lang.StringUtils;
import org.junit.After;
import org.junit.Before;
import org.junit.experimental.categories.Category;

/**
 * If you want to run this test locally, you need to run {@link LocalTestSetup} first
 *
 * @author bardhi
 * @since 5/8/15.
 */
@Category(MongoDbTests.class)
public class MongoVersionedRepoTest extends VersionedRepoTest {

    @Before
    public void setUp() throws Exception {
        super.setUp();
    }

    @Override
    @After
    public void tearDown() throws Exception {
        super.tearDown();
    }

    @Override
    protected boolean getUsesCleanupService() {
        return true;
    }

    @Override
    protected String getConfig(String authority) {
        return String.format("NREP {} USING MONGO { prefix=\"%s\"} on integrationTest", StringUtils.stripStart(authority, "/"));
    }

}
