CREATE OR REPLACE FUNCTION public.rap_insertF_%1$s(f character, t character, nature text)
 RETURNS folderType
 LANGUAGE plpgsql
AS $function$ 
DECLARE
   version folderType;
BEGIN
  SELECT INTO version N FROM %2$s WHERE ID=f AND SUB=t;
  IF NOT FOUND THEN
        INSERT INTO %2$s VALUES(f, t, nature::folderType);
  END IF;
  return version;
END;$function$
