package rapture.metrics.cache;

import rapture.metrics.store.MetricsStore;

/**
 * @author bardhi
 * @since 3/31/15.
 */
public class Gauge implements Metric {
    private Double doubleValue;
    private String parameterName;

    public Double getDoubleValue() {
        return doubleValue;
    }

    public void setDoubleValue(Double doubleValue) {
        this.doubleValue = doubleValue;
    }

    public Long getLongValue() {
        return longValue;
    }

    public void setLongValue(Long longValue) {
        this.longValue = longValue;
    }

    private Long longValue;

    public String getParameterName() {
        return parameterName;
    }

    public void setParameterName(String parameterName) {
        this.parameterName = parameterName;
    }

    @Override
    public void storeMe(MetricsStore metricsStore) {
        if (doubleValue != null) {
            metricsStore.recordGaugeValue(parameterName, doubleValue);
        } else if (longValue != null) {
            metricsStore.recordGaugeValue(parameterName, longValue);
        }
    }
}
